import React from 'react';
import '../stylesheets/App.scss';

class Filter extends React.Component {
  constructor (props) {
    super (props);
   this.searchCharacter = this.searchCharacter.bind(this)
}
searchCharacter (event) {
  const value = event.target.value;
  this.props.handleInpu (value) //funcion que avisa a app de que se activa el evento
}

  render() {
    return (
      <form>
            <label htmlFor="title"></label>
            <input
                type="text"
                id="title"
                name="title"
                value= {this.props.value} // pasa por props el nuevo valor del imput (el que el usuario busca)
                onChange= {this.searchCharacter} //Evento para el filtrado.
            />
        </form>
    );
  }
}

export default Filter;
