import React from 'react';
import '../stylesheets/App.scss';
import CharacterCard from './CharacterCard';

class CharacterList extends React.Component {
  render() {
    //para pintar la lista
    const mortyCards = this.props.dataMortylist.results; //props que me pasa el array de datos
    const itemsMortyCards = mortyCards.map((card) => {
      return (
        <li key={card.id}>
          <CharacterCard
            name={card.name}
            image={card.image}
            species={card.species}
          />
        </li>
      );
    });
    //para filtrar la lista

    const inputValue = this.props.inputValue //props que me pasa el value que pone el usuario (que esta en el estado)
    const filterMortyCard = mortyCards.filter(card => inputValue === "" || card.name.toLowerCase().includes(inputValue.toLowerCase()) ) 

    return ( <ul>{itemsMortyCards}</ul>)

